.guide-box {
    padding: 20px;
    text-align: left;
    margin-bottom: 44px !important;
    background: #f5eee6;
    box-shadow: 2px 2px 4px #d6d6d6;
}
.guide-button {
    padding: 10px 5px;
    font-size: 16px;
    background-color: #ffd401;
    color: #232323;
    border-color: transparent;
    font-weight: 600;
    border-radius: 4px;}
.post-content a:hover{text-decoration:none;}
